export const currencyFormatter = (num, digits) => {
	var si = [
		{ value: 1, symbol: "" },
		{ value: 1E3, symbol: "k" },
		{ value: 1E6, symbol: "M" },
		{ value: 1E9, symbol: "G" },
		{ value: 1E12, symbol: "T" },
		{ value: 1E15, symbol: "P" },
		{ value: 1E18, symbol: "E" }
	];
	var rx = /\.0+$|(\.[0-9]*[1-9])0+$/;
	var i;
	for (i = si.length - 1; i > 0; i--) {
		if (num >= si[i].value) {
		break;
		}
	}
	return (num / si[i].value).toFixed(digits).replace(rx, "$1") + si[i].symbol;
}

export const UScurrencyFormatter = (value) => {
	let dollarUSLocale = Intl.NumberFormat('en-US', { style: 'decimal', maximumFractionDigits: 2, minimumFractionDigits: 2 });
	return dollarUSLocale.format(value);
}

export const timeFormatter = (seconds) => {
	var result = ''
	while (seconds > 60) {
		if (seconds >= 86400) {
		let days = Math.floor(seconds / 86400)
		result += (result !== '' ? ':' : '') + days + 'd'
		seconds -= (days * 86400)
		}
		if (seconds >= 3600) {
		let hours = Math.floor(seconds / 3600)
		result += (result !== '' ? ':' : '') + ('0' + hours).slice(-2) + 'h'
		seconds -= (hours * 3600)
		}
		if (seconds >= 60) {
		let minutes = Math.floor(seconds / 60)
		result += (result !== '' ? ':' : '') + ('0' + minutes).slice(-2) + 'm'
		seconds -= (minutes * 60)
		}
	}
	result += (result !== '' ? ':' : '') + ('0' + seconds).slice(-2) + 's'
	return result
}

